      当前穿着: z.string()
    })
  ).prefault({}),

  重要角色: z.record(
    z.string().describe('角色名'),
    z.object({
      修为: z.string(),
      好感度: z.coerce.number().transform(v => _.clamp(v, 0, 100)),
      功法: z.record(
        z.string().describe('功法名'),
        z.object({
          品阶: z.string(),
          描述: z.string()
        })
      ).prefault({}),
      法宝: z.record(
        z.string().describe('法宝名'),
        z.object({
          品阶: z.string(),
          描述: z.string()
        })
      ).prefault({})
    })
  ).prefault({})
});

$(() => {
  registerMvuSchema(Schema);
})
