<body>
<div class="status-card" id="status-card">
    <header class="status-header">
        <div class="status-title">
            <h1>每天回家都看到妈妈在COSPLAY</h1>
            <p class="status-subtitle">夏川星来的日常</p>
        </div>
        <div class="status-meta">
            <div class="meta-item">
                <span class="meta-label">当前时间</span>
                <span class="meta-value" id="current-time">N/A</span>
            </div>
            <div class="meta-item">
                <span class="meta-label">当前地点</span>
                <span class="meta-value" id="current-location">N/A</span>
            </div>
        </div>
    </header>

    <section class="panel-block">
        <h3 class="panel-title">夏川星来</h3>
        <div class="text-group">
            <div class="text-item">
                <span class="info-label">当前着装</span>
                <p class="info-text" id="seira-outfit">N/A</p>
            </div>
            <div class="text-item">
                <span class="info-label">当前姿势</span>
                <p class="info-text" id="seira-pose">N/A</p>
            </div>
            <div class="text-item">
                <span class="info-label">当前想法</span>
                <p class="info-text" id="seira-thoughts">N/A</p>
            </div>
        </div>
    </section>

    <section class="panel-block">
        <div style="display: flex; align-items: center; gap: 12px;">
            <h3 class="panel-title">COSPLAY状态</h3>
            <span id="cos-status">N/A</span>
        </div>
        <div class="text-group">
            <div class="text-item">
                <span class="info-label">角色设定</span>
                <p class="info-text" id="cos-character">N/A</p>
            </div>
            <div class="text-item">
                <span class="info-label">服装设定</span>
                <p class="info-text" id="cos-costume">N/A</p>
            </div>
        </div>
    </section>
</div>

<script>
    function SafeGetValue(obj, path, defaultValue = "N/A") {
        let keys = Array.isArray(path) ? path : path.split('.');
        let current = obj;
        for (let i = 0; i < keys.length; i++) {
            if (current === undefined || current === null || typeof current !== 'object' || !current.hasOwnProperty(keys[i])) {
                return defaultValue;
            }
            current = current[keys[i]];
        }
        if (current === undefined || current === null) {
            return defaultValue;
        }
        if (Array.isArray(current)) {
            if (current.length > 0) {
                const actualValue = current[0];
                if (typeof actualValue === 'boolean') {
                    return actualValue;
                }
                return String(actualValue);
            } else {
                return defaultValue;
            }
        }
        if (typeof current === 'boolean') {
            return current;
        }
        if (typeof current === 'number') {
            return current;
        }
        if (typeof current === 'string') {
            return current;
        }
        return JSON.stringify(current);
    }

    async function initDisplay() {
        try {
            const messages = await getChatMessages(getCurrentMessageId());
            if (!messages || messages.length === 0 || !messages[0].data) {
                document.getElementById('status-card').innerHTML = "<div class='error-tip'>无法加载状态数据 (messages empty)</div>";
                return;
            }
            const gameData = messages[0].data;
            const characterData = gameData.stat_data;
            if (!characterData) {
                document.getElementById('status-card').innerHTML = "<div class='error-tip'>无法加载状态数据 (characterData missing)</div>";
                return;
            }

            const timeElem = document.getElementById('current-time');
            if (timeElem) timeElem.innerText = SafeGetValue(characterData, '世界.当前时间');

            const locationElem = document.getElementById('current-location');
            if (locationElem) locationElem.innerText = SafeGetValue(characterData, '世界.当前地点');

            const seiraOutfit = document.getElementById('seira-outfit');
            if (seiraOutfit) seiraOutfit.innerText = SafeGetValue(characterData, '夏川星来.当前着装');

            const seiraPose = document.getElementById('seira-pose');
            if (seiraPose) seiraPose.innerText = SafeGetValue(characterData, '夏川星来.当前姿势');

            const seiraThoughts = document.getElementById('seira-thoughts');
            if (seiraThoughts) seiraThoughts.innerText = SafeGetValue(characterData, '夏川星来.当前想法');

            const cosStatusValue = SafeGetValue(characterData, 'COSPLAY.状态开关');
            const cosStatusElem = document.getElementById('cos-status');
            if (cosStatusElem) {
                const isActive = cosStatusValue === true || cosStatusValue === 'true';
                const badgeClass = isActive ? 'cos-badge-active' : 'cos-badge-inactive';
                const statusText = isActive ? 'ON' : 'OFF';
                cosStatusElem.innerHTML = `<span class="cos-badge ${badgeClass}">${statusText}</span>`;
            }

            const cosCharacter = document.getElementById('cos-character');
            if (cosCharacter) cosCharacter.innerText = SafeGetValue(characterData, 'COSPLAY.角色设定');

            const cosCostume = document.getElementById('cos-costume');
            if (cosCostume) cosCostume.innerText = SafeGetValue(characterData, 'COSPLAY.服装设定');

        } catch (error) {
            document.getElementById('status-card').innerHTML = `<div class='error-tip'>状态栏加载出错: ${error.message}</div>`;
        }
    }

    document.addEventListener('DOMContentLoaded', initDisplay);
</script>
</body>
</html>

